LOCK TABLES `sp_chapter` WRITE;
/*!40000 ALTER TABLE `sp_chapter` DISABLE KEYS */;
INSERT INTO `sp_chapter` VALUES (1,1,'软件项目管理概述','介绍项目、软件项目、项目管理、软件项目管理等概念；介绍项目管理知识体系以及软件项目管理路线图以及课程思路','chapter_1.png','1.ppt'),(2,2,'软件项目确立','介绍立项过程，项目招投标流程，项目章程概念，以及举例分析具体案例','chapter_2.png','2.ppt'),(3,3,'软件生存期模型','介绍了生存期模型定义和特征以及各种软件生存模型，举例分析具体案例','chapter_3.png','3.ppt'),(4,4,'软件项目需求管理','定义软件需求，介绍需求过程和建模方法，举例分析具体案例','chapter_4.png','4.ppt'),(5,5,'软件项目任务分解','定义任务分解、WBS、工作包等概念；介绍任务分解的4个方法，举例分析具体案例','chapter_5.png','5.ppt'),(6,6,'软件项目成本计划 ','介绍估算基本概念以及各种估算方法；举例分析具体案例','chapter_6.png','6.ppt'),(7,7,'软件项目进度计划','介绍任务、任务关系、进度图示等基本概念；各种历时估算法以及进度编排中的方法，举例分析具体案例','chapter_7.png','7.ppt'),(8,8,'软件项目质量计划  ','定义软件质量相关概念以及管理的三个过程；如何编写质量管理计划，举例分析具体案例','chapter_8.png','8.ppt'),(9,9,'软件配置管理计划','定义软件配置管理相关概念和过程；举例分析具体案例','chapter_9.png','9.ppt'),(10,10,'软件项目人员与沟通计划 ','介绍组织结构、RAM、人员安排计划，干系人定义、干洗人识别、干洗人管理计划和沟通形式、沟通渠道、沟通计划；举例分析具体案例','chapter_10.png','10.ppt'),(11,11,'软件项目风险计划','介绍风险管理的基本概念；风险识别，风险评估，风险规划的过程；举例分析具体案例','chapter_11.png','11.ppt'),(12,12,'软件项目合同计划 ','介绍合同的基本概念；举例分析具体案例','chapter_12.png','12.ppt'),(13,13,'软件项目集成计划','介绍软件项目集成计划定义；举例分析具体案例','chapter_13.png','13.ppt'),(14,14,'软件项目集成执行控制','介绍项目执行控制流程、项目数据采集、集成变更管理；举例分析具体案例','chapter_14.png','14.ppt'),(15,15,'核心计划执行控制','介绍项目成本、进度执行，项目质量计划控制；举例分析具体案例','chapter_15.png','15.ppt'),(16,16,'辅助计划执行控制','介绍人员管理，干系人管理，项目进行过程中的沟通管理、风险管理、合同管理；举例分析具体案例','chapter_16.png','16.ppt'),(17,17,'项目结束过程','介绍合同结束和项目结束，举例分析具体案例','chapter_17.png','17.ppt');
/*!40000 ALTER TABLE `sp_chapter` ENABLE KEYS */;
UNLOCK TABLES;

